<?php

namespace CD\PlatformBundle\Repository;

/**
 * Traduction_TargetRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Traduction_TargetRepository extends \Doctrine\ORM\EntityRepository
{
	public function countSourcesTranslatedByUserIdForProject($userId, $projectId)
	{
		return $this->createQueryBuilder('u')
		->select('COUNT(u)')
		->where('u.author = :uid')
        ->leftJoin('u.source', 'source')
        ->andWhere('source.project = :pid')
		->setParameter('uid', $userId)
        ->setParameter('pid', $projectId)
		->getQuery()
		->getSingleScalarResult();
	}

    public function countSourcesTranslatedByUserIdAtDate($userId, $date)
    {
        $from = new \DateTime($date->format("Y-m-d")." 00:00:00");
        $to   = new \DateTime($date->format("Y-m-d")." 23:59:59");

        return $this->createQueryBuilder('u')
        ->select('COUNT(u)')
        ->where('u.author = :uid')
        ->andWhere('u.date BETWEEN :from AND :to')
        ->setParameter('uid', $userId)
        ->setParameter('from', $from)
        ->setParameter('to', $to)
        ->getQuery()
        ->getSingleScalarResult();
    }
}
